script:
frappe.ui.form.on('Packing Slip', {
	box_no(frm) {
    	if(frm.doc.items != undefined){
    			for(var i =0;i<cur_frm.doc.items.length;i++){
    				cur_frm.doc.items[i].box_no=cur_frm.doc.box_no
    			}
    		}
    	}
})

frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
	    if(frm.doc.delivery_note == undefined)
		$.each(frm.doc.items,function(idx, item){
            if(item.delivery_note != undefined){
                frm.set_value("delivery_note",item.delivery_note)
            }
		});
		if(frm.doc.financial_year == undefined){
    		frappe.db.get_value("Fiscal Year",{"disabled":0,"year_start_date":["<=",frm.doc.posting_date],"year_end_date":[">=",frm.doc.posting_date]},"name",(y)=>{
    		        frm.set_value("financial_year",y.name)
    		})
		}
	}
})

frappe.ui.form.on('Stock Entry', {
	refresh(frm) {
	},
	before_submit(frm){
	    if(frm.doc.stock_entry_type=="Material Receipt"){
    	    $.each(frm.doc.items, function(idx,item){
    	       frappe.call({
    		    type:"POST",
    			args:{
    				'item_code': item.item_code
    			},
    			async:false,
    			url: "/api/method/insert_barcode",
    			callback: function(r){

    			}
    		})
    	  })
	    }
	}
})

frappe.ui.form.on('Stock Reconciliation', {
	refresh(frm) {
	},
	before_submit(frm){
	    if(frm.doc.purpose=="Opening Stock"){
    	    $.each(frm.doc.items, function(idx,item){
    	       frappe.call({
    		    type:"POST",
    			args:{
    				'item_code': item.item_code
    			},
    			async:false,
    			url: "/api/method/insert_barcode",
    			callback: function(r){
                    console.log(r)
    			}
    		})
    	  })
	    }
	}
})

frappe.ui.form.on('Item Attribute', {
	refresh(frm) {
	    var abbr_field = frappe.meta.get_docfield("Item Attribute Value","abbr", cur_frm.doc.name);
            abbr_field.set_only_once = 1;
	},
	before_save: function(frm,cdt,cdn){
	    $.each(frm.doc.item_attribute_values, function(idx,item){
	       var name=frm.doc.name;
	       var str = name.toLowerCase();
	       if(str.includes("size")){
    	    if(item.abbr.length>=4){
    	        frappe.validate=false;
    	        frappe.throw(__("The abbreviation length should be less than 3 characters. The maximum field length has been exceeded, please update."))
    	    }
    	}
	       if(str.includes("colour")){
    	    if(item.abbr.length>=3){
    	        frappe.validate=false;
    	        frappe.throw(__("The abbreviation length should be less than 2 characters. The maximum field length has been exceeded, please update."))
    	    }
    	}
	       if(str.includes("color")){
    	    if(item.abbr.length>=3){
    	        frappe.validate=false;
    	        frappe.throw(__("The abbreviation length should be less than 2 characters. The maximum field length has been exceeded, please update."))
    	    }
    	}
	    })
	}
})

frappe.ui.form.on('Delivery Note', {
refresh: function(frm) {
set_css(frm);
}
});

var set_css = function (frm) {
    $('input[data-fieldname="tracking_id"]').css("color","blue")
}

frappe.ui.form.on('Item', {
    refresh: function(frm){
        if(frm.doc.variant_of != undefined){
            frappe.db.get_value("Item",{"name":frm.doc.variant_of},["brand_code","launch_year","collection_code","style_number","length","sleeve_length","cluster","wash","craft","fit","neck","fabric"],(i)=>{
               if(frm.doc.brand_code == undefined){ frm.set_value("brand_code",i.brand_code); }
               if(frm.doc.launch_year == undefined){ frm.set_value("launch_year",i.launch_year);}
               if(frm.doc.collection_code == undefined){ frm.set_value("collection_code",i.collection_code);}
                if(frm.doc.style_number == undefined){frm.set_value("style_number",i.style_number);}
                if(frm.doc.length == undefined){frm.set_value("length",i.length);}
                if(frm.doc.sleeve_length == undefined){ frm.set_value("sleeve_length",i.sleeve_length);}
                if(frm.doc.cluster == undefined){ frm.set_value("cluster",i.cluster);}
                if(frm.doc.wash == undefined){ frm.set_value("wash",i.wash);}
                if(frm.doc.craft == undefined){ frm.set_value("craft",i.craft);}
                if(frm.doc.fit == undefined){ frm.set_value("fit",i.fit);}
                if(frm.doc.neck == undefined){ frm.set_value("neck",i.neck);}
                if(frm.doc.fabric == undefined){ frm.set_value("fabric",i.fabric);}
            });
        }
        if(frm.doc.has_variants != 1 && frm.doc.__islocal != 1){
            frm.add_custom_button("Generate Barcode", function() {
		        frappe.call({
    		    type:"POST",
    			args:{
    				'item_code': frm.doc.item_code
    			},
    			async:false,
    			url: "/api/method/insert_barcode",
    			callback: function(r){

    			}
    		})
    		location.reload();
	        })
        }
    },
	before_save: function(frm){
	    if(frm.doc.variant_of == undefined && frm.doc.is_sales_item == 1){
	        var brand = frm.doc.brand_code;
	        var year = frm.doc.launch_year;
	        var collection = frm.doc.collection_code;
	        var sub_category;
	        var category;
	        var style = frm.doc.style_number;
	        var res;
	        frappe.db.get_value("Item Group",{"name":frm.doc.item_group},["sub_category_code","parent_item_group"],(s)=>{
	          sub_category=s.sub_category_code;
	          frappe.db.get_value("Item Group",{"name":s.parent_item_group},"category_code",(c)=>{
	              category=c.category_code
	              if(category == undefined){
	             res = brand.concat(year,collection,sub_category,style)
	              }else{
	             res = brand.concat(year,collection,category,sub_category,style)
	              }
	              frm.set_value("item_code",res);
	              frm.refresh_field("item_code");
	          })
	        });
	    }
	    else{}
	    var count = 0;
	    if(frm.doc.has_variants == 1){
	        $.each(frm.doc.attributes, function(idx,item){
	            var name=item.attribute;
	            var str = name.toLowerCase();
	            if(str.includes("size")){
	                count= count +1
	            }
	            if(str.includes("colour") || str.includes("color")){
	                count= count +1
	            }
	        })
	        if(count<2){
	            frappe.validate=false;
	            frappe.throw(__("For variants item, attributes table should have both colour and size attributes."))
	        }
	    }
	}
})

frappe.ui.form.on('Item Barcode', {
	refresh(frm) {
	},
	before_barcodes_remove: function(frm, cdt, cdn) {
	    var row = locals[cdt][cdn];
	    if(row.system_generated_barcode==1){
    	    frappe.validate = false;
    	 	frappe.throw(__("Unable to delete the system generated Barcode."));
	    }
	}
})

frappe.ui.form.on('POS Invoice', {
    validate(frm){
        if(frm.doc.transaction_id!=null && frm.doc.transaction_id!=undefined){
	        for(var i=0;i<frm.doc.payments.length;i++){
	            if(frm.doc.payments[i].mode_of_payment=="Online" && frm.doc.payments[i].mode_of_payment!=undefined && frm.doc.payments[i].base_amount>0){
	                frm.doc.payments[i].transaction_id=frm.doc.transaction_id
	            }
	        }
	    }
	    if(frm.doc.payment_source!=null && frm.doc.payment_source!=undefined){
	        for(var k=0;k<frm.doc.payments.length;k++){
	            if(frm.doc.payments[k].mode_of_payment=="Online" && frm.doc.payments[k].mode_of_payment!=undefined && frm.doc.payments[k].base_amount>0){
	                frm.doc.payments[k].payment_source=frm.doc.payment_source
	            }
	        }
	    }
		if (frm.doc.payments!=null) {
			for(var j=0;j<frm.doc.payments.length;j++){
	            if(frm.doc.payments[j].mode_of_payment=="Online" && frm.doc.payments[j].mode_of_payment!=undefined && frm.doc.payments[j].base_amount>0){
	                if(frm.doc.payments[j].transaction_id==null||frm.doc.payments[j].transaction_id==undefined||frm.doc.payments[j].transaction_id==''){
	                    frappe.msgprint(__("Unable to submit the invoice due to unavailability of Transaction Id. Please provide the transaction Id to submit the invoice"));
			            frappe.validated = false;
	                }
	            }
	        }
		}
	}
})
