script:
item_code=frappe.form_dict.get("item_code")
docval=frappe.db.get_value("Item Barcode",{"parent":item_code,"parenttype":"Item","barcode":item_code},"barcode")
if not docval:
    doc = frappe.get_doc('Item',item_code )
    doc.append('barcodes', {
    'barcode': item_code,
    'system_generated_barcode': 1
    })
    doc.save()
    frappe.response['message']=doc
else:
    frappe.response['message'] = "barcode not inserted"

if doc.variant_of and doc.is_sales_item == 1 and doc.fabric_or_yarn != 1:
    if doc.updated_item == 0:
        for attribute_list in doc.attributes:
            abbr=frappe.db.get_value("Item Attribute Value",{"parent":attribute_list.attribute,"attribute_value":attribute_list.attribute_value,"parenttype":"Item Attribute"},"abbr")
            if("colour" in attribute_list.attribute.lower() or "color" in attribute_list.attribute.lower()):
                color_abbr=abbr
            elif("size" in attribute_list.attribute.lower()):
                size_abbr=abbr
        res = str(doc.variant_of)+str(color_abbr)+"-"+str(size_abbr)
        doc.item_code=res
        doc.updated_item = 1

intermediate_product = 0        
if doc.variant_of:
    intermediate_product = frappe.db.get_value("Item",{"name":doc.variant_of},"intermediate_product")
if doc.variant_of and doc.is_sales_item == 0 and intermediate_product == 1 and doc.fabric_or_yarn != 1:
    if doc.updated_item == 0:
        for attribute_list in doc.attributes:
            abbr=frappe.db.get_value("Item Attribute Value",{"parent":attribute_list.attribute,"attribute_value":attribute_list.attribute_value,"parenttype":"Item Attribute"},"abbr")
            if("colour" in attribute_list.attribute.lower() or "color" in attribute_list.attribute.lower()):
                color_abbr=abbr
            elif("size" in attribute_list.attribute.lower()):
                size_abbr=abbr
            elif("stage" in attribute_list.attribute.lower()):
                stage_abbr=abbr
        res = str(doc.variant_of)+str(color_abbr)+"-"+str(size_abbr)+"-"+str(stage_abbr)
        doc.item_code=res
        doc.updated_item = 1

if doc.variant_of and doc.fabric_or_yarn == 1:
    if doc.updated_item == 0:
        for attribute_list in doc.attributes:
            abbr=frappe.db.get_value("Item Attribute Value",{"parent":attribute_list.attribute,"attribute_value":attribute_list.attribute_value,"parenttype":"Item Attribute"},"abbr")
            if("colour" in attribute_list.attribute.lower() or "color" in attribute_list.attribute.lower()):
                color_abbr=abbr
            elif("width" in attribute_list.attribute.lower() or "form" in attribute_list.attribute.lower()):
                size_abbr=abbr
        res = str(doc.variant_of)+"-"+str(color_abbr)+"-"+str(size_abbr)
        doc.item_code=res
        doc.updated_item = 1

warehouse_list=[]
for item in doc.items:
    if item.warehouse not in warehouse_list:
        warehouse_list.append(item.warehouse)
send_email_flag=False

for warehouse in warehouse_list:
    warehouse_manager_user=frappe.db.get_value('Warehouse',{'name':warehouse},'warehouse_manager')
    if warehouse_manager_user:
        email_address=frappe.db.get_value('User',{'name':warehouse_manager_user},'email')
        first_name=frappe.db.get_value('User',{'name':warehouse_manager_user},'first_name')
        item_list=frappe.db.get_list('Sales Order Item',
        filters={
            'parent':doc.name,
            'warehouse':warehouse
        },
        fields=['item_name','name']
        )
        if item_list:
            content1="Dear "+first_name+","+"<br>"+"<br>"+"This is to inform you that the Ordered Quantity for "
            for items in item_list:
                item_doc=frappe.get_doc('Sales Order Item',items.name) 
                if item_doc:
                    if item_doc.qty>=item_doc.actual_qty:
                        content1=content1+"<b>"+ items.item_name+"</b>"+","
                        send_email_flag=True
            if send_email_flag==True:
                frappe.sendmail(recipients=email_address,
            		subject="Stock Availability Notification",
            		message= content1+" is more than the available quantity at "+"<b>"+warehouse+"</b>"+". Please follow with the team to avoid any shortage. "+"<br>"+"<br>"+
            "Thanks and Regards,"+"<br>"+"<b>"+
        doc.company+"</b>")

if doc.purpose=="Opening Stock":
    for items in frappe.db.get_list("Stock Reconciliation Item",filters={"parenttype":"Stock Reconciliation","parent":doc.name},fields={"item_code"}):
        docval=frappe.db.get_value("Item Barcode",{"parent":items.item_code,"parenttype":"Item","barcode":items.item_code},"barcode")
        if not docval:
            item_doc = frappe.get_doc('Item',items.item_code )
            item_doc.append('barcodes', {
            'barcode': items.item_code,
            'system_generated_barcode': 1
            })
            item_doc.save()

if doc.stock_entry_type=="Material Receipt":
    for items in frappe.db.get_list("Stock Entry Detail",filters={"parenttype":"Stock Entry","parent":doc.name},fields={"item_code"}):
        docval=frappe.db.get_value("Item Barcode",{"parent":items.item_code,"parenttype":"Item","barcode":items.item_code},"barcode")
        if not docval:
            item_doc = frappe.get_doc('Item',items.item_code )
            item_doc.append('barcodes', {
            'barcode': items.item_code,
            'system_generated_barcode': 1
            })
            item_doc.save()
